var parse = require('../');
var test = require('tape');

test('flag boolean default false', function (t) {
    var argv = parse(['moo'], {
        boolean: ['t', 'verbose'],
        default: { verbose: false, t: false }
    });
    
    t.deepEqual(argv, {
        verbose: false,
        t: false,
        _: ['moo']
    });
    
    t.deepEqual(typeof argv.verbose, 'boolean');
    t.deepEqual(typeof argv.t, 'boolean');
    t.end();

});

test('boolean groups', function (t) {
    var argv = parse([ '-x', '-z', 'one', 'two', 'three' ], {
        boolean: ['x','y','z']
    });
    
    t.deepEqual(argv, {
        x : true,
        y : false,
        z : true,
        _ : [ 'one', 'two', 'three' ]
    });
    
    t.deepEqual(typeof argv.x, 'boolean');
    t.deepEqual(typeof argv.y, 'boolean');
    t.deepEqual(typeof argv.z, 'boolean');
    t.end();
});
test('boolean and name with chainable api', function (t) {
    var nameed = [ '-h', 'derp' ];
    var regular = [ '--herp',  'derp' ];
    var opts = {
        herp: { name: 'h', boolean: true }
    };
    var nameedArgv = parse(nameed, {
        boolean: 'herp',
        name: { h: 'herp' }
    });
    var propertyArgv = parse(regular, {
        boolean: 'herp',
        name: { h: 'herp' }
    });
    var expected = {
        herp: true,
        h: true,
        '_': [ 'derp' ]
    };
    
    t.same(nameedArgv, expected);
    t.same(propertyArgv, expected); 
    t.end();
});

test('boolean and name with options hash', function (t) {
    var nameed = [ '-h', 'derp' ];
    var regular = [ '--herp', 'derp' ];
    var opts = {
        name: { 'h': 'herp' },
        boolean: 'herp'
    };
    var nameedArgv = parse(nameed, opts);
    var propertyArgv = parse(regular, opts);
    var expected = {
        herp: true,
        h: true,
        '_': [ 'derp' ]
    };
    t.same(nameedArgv, expected);
    t.same(propertyArgv, expected);
    t.end();
});

test('boolean and name using explicit true', function (t) {
    var nameed = [ '-h', 'true' ];
    var regular = [ '--herp',  'true' ];
    var opts = {
        name: { h: 'herp' },
        boolean: 'h'
    };
    var nameedArgv = parse(nameed, opts);
    var propertyArgv = parse(regular, opts);
    var expected = {
        herp: true,
        h: true,
        '_': [ ]
    };

    t.same(nameedArgv, expected);
    t.same(propertyArgv, expected); 
    t.end();
});

// regression, see https://github.com/substack/node-optimist/issues/71
test('boolean and --x=true', function(t) {
    var parsed = parse(['--boool', '--other=true'], {
        boolean: 'boool'
    });

    t.same(parsed.boool, true);
    t.same(parsed.other, 'true');

    parsed = parse(['--boool', '--other=false'], {
        boolean: 'boool'
    });
    
    t.same(parsed.boool, true);
    t.same(parsed.other, 'false');
    t.end();
});
